mapper.insert:

    SELECT LAST_INSERT_ID() AS id

insert into document_table (`user_id`,`case_id`,`status`, `identify`, `type`, `file_type`,`name`,`created_by`,`s3_location`, `auto_generated`, `created_at`, `updated_at`)
values(#{userId},#{caseId}, #{status}, #{identify}, #{type}, #{fileType}, #{name}, #{createdBy},#{s3Location},#{autoGenerated}, #{createdAt}, #{updatedAt});
