mapper.insert:

    SELECT LAST_INSERT_ID() AS id

insert into document_table (`user_id`,`case_id`,`status`,`identify`, `type`, `file_type`, `generation_type`,`name`,`description`,`created_by`,`s3_location`, `auto_generated`, `created_at`, `updated_at`)
values(#{userId},#{caseId},#{status}, #{identify}, #{type}, #{fileType}, #{generationType}, #{name}, #{description}, #{createdBy}, #{s3Location}, #{autoGenerated}, #{createdAt}, #{updatedAt});

mapper.select:
select * from document_table where case_id = #{caseId}

    and type = #{type}


    and identify = #{identify}


    and auto_generated = #{autoGenerated}


    and generation_type = #{generationType}
